
public class forEachLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int numbers[]= {1,2,3,4,5,6};
		
		for(int x:numbers)
		{
			System.out.println(x);
			
		}
		
	String arr[]= {"Hello","My","Name","Is","Daniel"};
		for (String x:arr)
		{
			System.out.println(x);
			
		}
		
	}

}
